actionConfiguration.body:
query check($id: String!) {
  assignment: people_assignment_v0(where: {FestCloudID: {_eq: $id}}) {
    JobTitle
    Role
    ManagerFestCloudId
    EmployeeFestCloudID
		FestCloudID
		disposition: disposition_assignmentfestcloudid_array {
      EndDate
    }
    workgroup: assignment_workgroupfestcloudid_workgroup {
      FestCloudID
      Name
    }
    position: assignment_positionfestcloudid_position {
      FestCloudID
      PositionName
    }
    manager: assignment_managerfestcloudid_employee {
      manager_employee: employee_festcloudid_person {
        Name
        FamilyName
      }
    }
    StartDate
    employee: assignment_employeefestcloudid_employee {
      PhotoLink
    }
    assigment_ext: assignment_festcloudid_assignmentext {
      CooperationType
			Token
    }
		content_items:contentitemvalue_assignmentfestcloudid_array {
			FestCloudID
      ContentItemFestCloudID
      Value
   		 content_item:contentitemvalue_contentitemfestcloudid_contentitem{
        ContentKey
      }
    }
  }
}
