actionConfiguration.body:
query Check($personId: String!) {
  identification: people_identificationdata_v0(where: 
    {PersonFestCloudID: {_eq: $personId}
    }) {
		MilitaryRegistration
    ResidenceAddressFestCloudID
    RegistrationAddressFestCloudID
    registration: identificationdata_registrationaddressfestcloudid_address {
      Country
      City
      Street
      PostalCode
      BuildingNumber
      Apartment
    }
    residence: identificationdata_residenceaddressfestcloudid_address {
      Country
      City
      Street
      PostalCode
      BuildingNumber
      Apartment
    }
  }
	customer:people_customer_v0(where: {FestCloudID: {_eq: $personId}}){
    LoyaltyCard
  }
	dms_passport:dms_document_v0(where: {
    document_festcloudid_personaliddata: {
      PersonFestCloudID: {_eq: $personId}
    }
  }, order_by: {AuditUpdatedAt: desc})  {
    passport:document_festcloudid_personaliddata {
      FestCloudID
      DocumentNumber
      DocumentSeries
            AuditUpdatedAt
            passport_document:personaliddata_festcloudid_document{
        document_taxonomy:document_taxonomyfestcloudid_taxonomy{
          Name
        }
      }
    }
  }
  dms_tax:dms_document_v0(where: {
    document_festcloudid_personaltaxdata: {
      PersonFestCloudID: {_eq: $personId}
    }
  }) {
    tax:document_festcloudid_personaltaxdata{
      FestCloudID
      IndividualTaxpayerNumber
    }
  }
}
